    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        jSpinner1.setValue(15);
        jLabel1.setText("Button1 ActionPerfomed Ahora vale 15");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        jSpinner1.setValue(((SpinnerNumberModel) jSpinner1.getModel()).getMinimum());
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        try {
            txtFecVencimiento.setValue(new SimpleDateFormat("dd/MM/yyyy").parse("30/12/2022"));
        } catch (ParseException ex) {
            Logger.getLogger(SpinnerTest.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        Date fecha = ((Date) txtFecVencimiento.getValue());
        jLabel1.setText(fecha.toString());
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jToggleButton1ItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_jToggleButton1ItemStateChanged
        if (jToggleButton1.isSelected()) {
            jSpinner1.setFocusable(false);
            jToggleButton1.setText("NO focusable");
        } else {
            jSpinner1.setFocusable(true);
            jToggleButton1.setText("focusable");
        }
    }//GEN-LAST:event_jToggleButton1ItemStateChanged

    private void jToggleButton2ItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_jToggleButton2ItemStateChanged
        if (jToggleButton2.isSelected()) {
            jSpinner1.setEnabled(false);
            jToggleButton2.setText("disabled");
        } else {
            jSpinner1.setEnabled(true);
            jToggleButton2.setText("enabled");
        }
    }//GEN-LAST:event_jToggleButton2ItemStateChanged

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int valor = (int) jSpinner1.getValue();
        jLabel1.setText("El valor es " + valor);
    }//GEN-LAST:event_jButton5ActionPerformed

